{"changed":true,"filter":false,"title":"users_controller.rb","tooltip":"/app/controllers/users_controller.rb","value":"class UsersController < ApplicationController\n  before_action :logged_in_user, only: [:index, :edit, :update, :destroy]\n  before_action :correct_user,   only: [:edit, :update]\n  before_action :admin_user,     only: :destroy\n  layout :choose_layout\n\n  def new\n    @user = User.new\n  end\n\n  def create\n      @user = User.new(user_params)\n\n      if @user.save\n        log_in @user\n        flash[:success] = \"Welcome to ForeverCard!\"\n        redirect_to @user\n    else\n      render 'new'\n    end\n  end\n\n  def edit\n    @user = User.find(params[:id])\n  end\n  \n  def show\n    @user = User.find_by_username(params[:username])\n  end\n  \n  def index\n    @users = User.all\n  end\n  \n  def update\n    @user = User.find(params[:id])\n    \n    info = user_params\n    if info[:password].empty?\n      info.delete 'password'\n    end\n \n    if @user.update_attributes(info)\n      redirect_to @user\n    else\n      render 'edit'\n      # Can change this to 'update' ?\n    end\nend\n\n  def destroy\n    User.find(params[:id]).destroy\n    flash[:success] = \"User deleted\"\n    redirect_to users_url\n  end\n  \n \n\n  \n  private\n  def user_params\n    params.require(:user).permit(:first, :last, :email, :username, :password, :org, :role, :phone, :bio, :facebook, :twitter, :linkedin)\n  end\n  \n  def choose_layout\n    if @user.nil? or @user.id.nil?\n      'application'\n    else\n      'logged_in'\n    end\n  end\n\n  # Confirms a logged-in user.\n  def logged_in_user\n    unless logged_in?\n      store_location\n      flash[:danger] = \"Please log in.\"\n      redirect_to root_url\n    end\n  end\n\n  # Confirms the correct user.\n  def correct_user\n    @user = User.find(params[:id])\n    redirect_to(root_url) unless current_user?(@user)\n  end\nend\n","undoManager":{"mark":46,"position":50,"stack":[[{"group":"doc","deltas":[{"start":{"r